SELECT  CM2DAEALink.*, [Transaction].TransactionType, CONVERT(VARCHAR(30),[Transaction].TransactionTimestamp, 126) as TransactionTimestamp FROM  CM2DAEALink INNER JOIN [Transaction] ON CM2DAEALink.TransactionId = [Transaction].TransactionID INNER JOIN SubmissionStatus ON CM2DAEALink.ComplianceMonitoringId = SubmissionStatus.ID And SubmissionStatus.TableName='CM2DAEALink' And SubmissionStatus.Status is null AND SubmissionStatus.operation <> 'delete' AND [Transaction].TransactionID = SubmissionStatus.TransactionID Where ComplianceMonitoringId ='{$ComplianceMonitoringId}'