SELECT  CM2FederalEALink.*, [Transaction].TransactionType, CONVERT(VARCHAR(30),[Transaction].TransactionTimestamp, 126) as TransactionTimestamp FROM  CM2FederalEALink INNER JOIN [Transaction] ON CM2FederalEALink.TransactionId = [Transaction].TransactionID INNER JOIN SubmissionStatus ON CM2FederalEALink.ComplianceMonitoringId = SubmissionStatus.ID And SubmissionStatus.TableName='CM2FederalEALink' And SubmissionStatus.Status is null AND SubmissionStatus.operation <> 'delete' AND [Transaction].TransactionID = SubmissionStatus.TransactionID Where ComplianceMonitoringId ='{$ComplianceMonitoringId}'