SELECT  CaseFile2CaseFileLink.*, [Transaction].TransactionType, CONVERT(VARCHAR(30),[Transaction].TransactionTimestamp, 126) as TransactionTimestamp FROM  CaseFile2CaseFileLink INNER JOIN [Transaction] ON CaseFile2CaseFileLink.TransactionId = [Transaction].TransactionID INNER JOIN SubmissionStatus ON CaseFile2CaseFileLink.CaseFileId = SubmissionStatus.ID And SubmissionStatus.TableName='CaseFile2CaseFileLink' And SubmissionStatus.Status is null AND SubmissionStatus.operation <> 'delete' AND [Transaction].TransactionID = SubmissionStatus.TransactionID Where CaseFileId ='{$CaseFileId}'