SELECT  CaseFile2FederalEALink.*, [Transaction].TransactionType, CONVERT(VARCHAR(30),[Transaction].TransactionTimestamp, 126) as TransactionTimestamp FROM  CaseFile2FederalEALink INNER JOIN [Transaction] ON CaseFile2FederalEALink.TransactionId = [Transaction].TransactionID INNER JOIN SubmissionStatus ON CaseFile2FederalEALink.CaseFileId = SubmissionStatus.ID And SubmissionStatus.TableName='CaseFile2FederalEALink' And SubmissionStatus.Status is null AND SubmissionStatus.operation <> 'delete' AND [Transaction].TransactionID = SubmissionStatus.TransactionID Where CaseFileId ='{$CaseFileId}'