SELECT A.ComplianceMonitoringStrategyID, A.AirFacilityId,A.AirCMSMinimumFrequency, A.AirCMSSourceCategoryCode,A.AirCMSStartDate, A.AirActiveCMSPlanIndicator, A.AirRemovedPlanDate, A.Comments, A.TransactionID,[Transaction].TransactionType,CONVERT(VARCHAR(30),[Transaction].TransactionTimestamp, 126) as TransactionTimestamp FROM  AirComplianceMonitoringStrategy A INNER JOIN [Transaction] ON A.TransactionID = [Transaction].TransactionID INNER JOIN SubmissionStatus ON A.AirFacilityID = SubmissionStatus.ID WHERE SubmissionStatus.TableName='AirComplianceMonitoringStrategy' AND SubmissionStatus.Status is NULL AND SubmissionStatus.Operation <> 'delete' AND SubmissionStatus.ID in ('{$AirFacilityID}') AND [Transaction].TransactionTimestamp between '{$fromDate}' and '{$toDate}'