SELECT  DAEA2DAEALink.*, [Transaction].TransactionType, CONVERT(VARCHAR(30),[Transaction].TransactionTimestamp, 126) as TransactionTimestamp FROM  DAEA2DAEALink INNER JOIN [Transaction] ON DAEA2DAEALink.TransactionId = [Transaction].TransactionID INNER JOIN SubmissionStatus ON DAEA2DAEALink.EnforcementActionId = SubmissionStatus.ID And SubmissionStatus.TableName='DAEA2DAEALink' And SubmissionStatus.Status is null AND SubmissionStatus.operation <> 'delete' AND [Transaction].TransactionID = SubmissionStatus.TransactionID Where EnforcementActionId ='{$EnforcementActionId}'