SELECT EnforcementActionMilestone.EnforcementActionMilestoneId, EnforcementActionMilestone.EnforcementActionId, EnforcementActionMilestone.MilestonePlannedDate,EnforcementActionMilestone.MilestoneActualDate, EnforcementActionMilestone.Type, EnforcementActionMilestone.TransactionID, [Transaction].TransactionType,CONVERT(VARCHAR(30),[Transaction].TransactionTimestamp, 126) as TransactionTimestamp FROM EnforcementActionMilestone INNER JOIN [Transaction] ON EnforcementActionMilestone.TransactionId = [Transaction].TransactionID INNER JOIN SubmissionStatus ON [Transaction].TransactionID = SubmissionStatus.TransactionID AND SubmissionStatus.TableName='EnforcementActionMilestone' AND SubmissionStatus.Status is NULL AND SubmissionStatus.Operation <> 'delete' WHERE  SubmissionStatus.ID in ('{$EnforcementActionMilestoneID}') AND [Transaction].TransactionTimestamp between '{$fromDate}' and '{$toDate}'