SELECT [Transaction].TransactionId, 'X' as TransactionType, CONVERT(VARCHAR(30),[Transaction].TransactionTimestamp, 126) as TransactionTimestamp, SubmissionStatus.ID as ComplianceMonitoringId, SubmissionStatus.ForeignKey as LinkedCMId FROM  [Transaction] INNER JOIN SubmissionStatus ON [Transaction].TransactionID = SubmissionStatus.TransactionID And SubmissionStatus.Status is null AND SubmissionStatus.operation = 'delete' AND SubmissionStatus.TableName ='CM2CMLink' Where ID ='{$ComplianceMonitoringId}'