SELECT  [Transaction].TransactionID, 'X' as TransactionType, CONVERT(VARCHAR(30),[Transaction].TransactionTimestamp, 126) as TransactionTimestamp, SubmissionStatus.ID as CaseFileId, SubmissionStatus.ForeignKey LinkedCaseFileId FROM  [Transaction] INNER JOIN SubmissionStatus ON [Transaction].TransactionID = SubmissionStatus.TransactionID And SubmissionStatus.Status is null AND SubmissionStatus.operation = 'delete' AND SubmissionStatus.TableName ='CaseFile2CaseFileLink' Where ID ='{$CaseFileId}'